package com.cosylab.logging.client.cache;

/**
 * The exceptions generated by the cache
 * 
 * @author acaproni
 *
 */
public class LogCacheException extends Exception {

	public LogCacheException() {
		super();
	}

	public LogCacheException(String message) {
		super(message);
	}

	public LogCacheException(String message, Throwable cause) {
		super(message, cause);
	}

	public LogCacheException(Throwable cause) {
		super(cause);
	}

}
